        fun isAlphaNumeric(s: String): Boolean {
            val pattern = "^[a-zA-Z0-9]*$"
            return s.matches(pattern.toRegex())
        }

        fun millisecondsToTime(milliseconds: Long): String {
            var hours = milliseconds / 1000 / 60 / 60
            val minutes = ( milliseconds / 1000 / 60) % 60
            val seconds = milliseconds / 1000 % 60
            val secondsStr = java.lang.Long.toString(seconds)
            val secs: String
            if (secondsStr.length >= 2) {
                secs = secondsStr.substring(0, 2)
            } else {
                secs = "0$secondsStr"
            }

            var decimalFormat = DecimalFormat("00")
            if (hours == 0L)
            return "${decimalFormat.format(minutes)}:${decimalFormat.format(seconds)}"
            else
                return "${decimalFormat.format(hours)}:${decimalFormat.format(minutes)}:${decimalFormat.format(seconds)}"
        }

